with sub as ( SELECT mstr.POLL_ID, GROUP_CONCAT(mstr.QESITM_SN order by FRST_REGISTER_PNTTM asc separator ",") as QESITM_SN, sub.POLL_STATUS FROM COMTNBBSPOOLATDRN mstr LEFT JOIN COMTNBBSPOOLMANAGE sub ON mstr.POLL_ID = sub.POLL_ID AND mstr.USER_ID = sub.USER_ID WHERE mstr.USER_ID = #{uniqId} GROUP BY mstr.POLL_ID ) SELECT mstr.POLL_ID, sub2.POLL_NM, mstr.POLL_BGNDE as startDate, mstr.POLL_ENDDE as endDate, case when sub.POLL_STATUS is null then 'todo' else sub.POLL_STATUS end as status, case when cast(mstr.POLL_ENDDE as date) < now() then true else false end expired FROM COMTNBBSPOLL mstr LEFT JOIN sub ON mstr.POLL_ID = sub.POLL_ID LEFT JOIN COMTNBBSPOLL sub2 ON mstr.POLL_ID = sub2.POLL_ID WHERE mstr.USE_AT = 'Y' ORDER BY mstr.POLL_ID
select POLL_ID, POLL_NM, POLL_BGNDE as startDate, POLL_ENDDE as endDate, case when cast(POLL_ENDDE as date) < cast(now() as date) then true else false end as expired from COMTNBBSPOLL
with sub as ( select mstr.POLL_ID, GROUP_CONCAT(concat(mstr.QESITM_SN, ':', mstr.QESITM_ANSWER) order by FRST_REGISTER_PNTTM desc separator ",") as QESITM_SN, sub.QESITM_SN_LIST, sub.POLL_STATUS from COMTNBBSPOOLATDRN mstr left join COMTNBBSPOOLMANAGE sub on mstr.POLL_ID = sub.POLL_ID and mstr.USER_ID = sub.USER_ID where mstr.USER_ID = #{uniqId} and mstr.POLL_ID = #{pollId} group by mstr.POLL_ID ) select mstr.POLL_ID, mstr.POLL_NM, mstr.POLL_BGNDE as startDate, mstr.POLL_ENDDE as endDate, mstr.TEMP_SAVE as isSave, mstr.VOICE_SUP as isVoice, case when sub.POLL_STATUS is null then 'todo' else sub.POLL_STATUS end as status, sub.QESITM_SN, sub.QESITM_SN_LIST from COMTNBBSPOLL mstr left join sub on mstr.POLL_ID = sub.POLL_ID where mstr.USE_AT = 'Y' and mstr.POLL_ID = #{pollId}
select a.POLL_ID as pollId, a.QESITM_SN as qesitmSn, a.QESITM_SJ as qesitmSj, a.QESITM_ANSWER_TEXT as qesitmAnswerText, a.QESITM_ANSWER_IMAGE as qesitmAnswerImage from COMTNBBSPOOLQESITM a where POLL_ID = #{pollId}
insert into COMTNBBSPOOLATDRN values ( #{pollId}, #{qesitmSn}, #{qesitmAnswer}, #{uniqId}, now() )
insert into COMTNBBSPOOLMANAGE values ( #{uniqId}, #{pollId}, #{qesitmSnList}, 'progress' )
update COMTNBBSPOOLATDRN set QESITM_ANSWER = #{qesitmAnswer} where USER_ID = #{uniqId} and POLL_ID = #{pollId}
update COMTNBBSPOOLMANAGE set POLL_STATUS = 'done' where USER_ID = #{uniqId} and POLL_ID = #{pollId}
select count(*) from COMTNBBSPOOLQESITM mstr where mstr.POLL_ID = #{pollId}
select case when count(*) > 0 then true else false end as isExist from COMTNBBSPOOLMANAGE a where a.USER_ID = #{uniqId} and a.POLL_ID = #{pollId} and a.POLL_STATUS = 'done'
select CASE WHEN count(*) > 0 THEN true ELSE false END as userExists from COMTNMBER where USER_ID = #{uniqId}
